Write-Host "Adding SSH known hosts..." -ForegroundColor Cyan
$sshPath = "$env:USERPROFILE\.ssh"
if(-not (Test-Path $sshPath)) {
    New-Item $sshPath -ItemType directory -Force
}

$contents = ""

# GitHub IP addresses
for($c = 252; $c -le 255; $c++) {
    for($d = 0; $d -le 255; $d++) {
        $contents += "github.com,192.30.$c.$d ssh-rsa `r`n"
    }
}

# BitBucket
$contents += "bitbucket.org,104.192.143.1 ssh-rsa 
bitbucket.org,104.192.143.2 ssh-rsa 
bitbucket.org,104.192.143.3 ssh-rsa 
bitbucket.org,104.192.143.65 ssh-rsa 
bitbucket.org,104.192.143.66 ssh-rsa 
bitbucket.org,104.192.143.67 ssh-rsa 
"

[IO.File]::WriteAllText("$sshPath\known_hosts", $contents)

Write-Host "Known hosts configured" -ForegroundColor Green
